from django import formsclass wifiForm(forms.Form):    name = forms.CharField(max_length=60, label='SSID')    password = forms.CharField(max_length=150, label='Password')    hidden = forms.BooleanField(required=False, label='Hidden')class linkForm(forms.Form):    item = forms.CharField(max_length=400, label='LInk')class telegramForm(forms.Form):    item = forms.CharField(max_length=400, label='Telegram URL')class instagramForm(forms.Form):    item = forms.CharField(max_length=400, label='INSTAGRAM URL')class twitterForm(forms.Form):    item = forms.CharField(max_length=400, label='Twittter URL')class facebookForm(forms.Form):    item = forms.CharField(max_length=400, label='Facebook URL')